package j2Operators;

public class As1_CalculateStudentMarks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Calculate the Total Marks and Average Marks of the Student.(Note: We have the 5 Subjects like "english = 84,hindi = 76,maths = 93,physics = 61,chemistry = 82)

		int English, Hindi, Maths, Physics, Chemistry,Total,Average;

		English = 84;
		Hindi = 76;
		Maths = 93;
		Physics = 61;
		Chemistry = 82;
		Total=English+Hindi+Maths+Physics+Chemistry;
		
		Average=Total/5;
		
		
		
		System.out.println("Total Marks are = "+Total);
		
		System.out.println("Average is  = "+ Average );
		
		
		  
		
		
		
		
		
	}

}
